.about {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: flex-start;
  -webkit-user-select: none;
  cursor: default;
  overflow: auto;
  text-align: center;
  font-size: 1.25em;
  line-height: 1.4;
  padding: 4em;
  color: #d8dee9;
  background-color: #2e3440;
}
.about button {
  cursor: default;
}
.about a:focus {
  color: inherit;
}
.about img,
.about a {
  -webkit-user-drag: none;
}
.about .group-start {
  margin-top: 4em;
}
.about .group-item {
  margin-top: 1.5em;
}
.about-container {
  width: 100%;
  max-width: 500px;
}
.about-atom-io:hover .about-logo {
  color: #40a977;
}
.about-logo {
  display: block;
  width: 100%;
  max-width: 280px;
  margin: 0 auto 1em auto;
  color: #eceff4;
  transition: color 0.2s;
}
.about-version-container:hover {
  color: #ffffff;
}
.about-version-container:active {
  color: #ffffff;
}
.about-version {
  margin-right: .5em;
  font-size: 1.25em;
  vertical-align: middle;
}
.about-header-release-notes {
  vertical-align: middle;
  margin-left: 1em;
}
.about-updates-box {
  display: flex;
  align-items: center;
  padding: 10px;
  border: 1px solid #3b4252;
  border-radius: 8px;
  background-color: #434c5e;
}
.about-updates-status {
  flex: 1;
  margin-left: .5em;
  text-align: left;
}
.about-updates-item,
.about-default-update-message .about-updates-label {
  display: block;
}
.about-updates-label {
  color: rgba(216, 222, 233, 0.4);
}
.about-updates-label.is-strong {
  color: #d8dee9;
}
.about-updates-version {
  margin: 0 .4em;
}
.about-updates-release-notes {
  margin: 0 1em 0 1.5em;
}
.about-auto-updates {
  margin-top: 1em;
}
.about-auto-updates input {
  margin-right: .5em;
}
.about-love .icon::before {
  position: relative;
  top: 1px;
  width: auto;
  margin-right: 0;
  font-size: 1.5em;
}
.about-love .icon-logo-github::before {
  top: 3px;
}
.about-credits {
  color: rgba(216, 222, 233, 0.4);
}
.about-release-notes {
  color: #88c0d0;
}
.about-release-notes:hover {
  color: #bfdde6;
}
