/*+++++++++++
  + Spinner +
  +++++++++++*/
.loading-spinner-large {
  width: 64px;
  height: 64px;
  display: block;
  background-image: url(images/octocat-spinner-128.gif);
  background-repeat: no-repeat;
  background-size: cover;
}
.loading-spinner-large.inline-block {
  display: inline-block;
}
.loading-spinner-medium {
  width: 48px;
  height: 48px;
  display: block;
  background-image: url(images/octocat-spinner-128.gif);
  background-repeat: no-repeat;
  background-size: cover;
}
.loading-spinner-medium.inline-block {
  display: inline-block;
}
.loading-spinner-small {
  width: 32px;
  height: 32px;
  display: block;
  background-image: url(images/octocat-spinner-128.gif);
  background-repeat: no-repeat;
  background-size: cover;
}
.loading-spinner-small.inline-block {
  display: inline-block;
}
.loading-spinner-tiny {
  width: 20px;
  height: 20px;
  display: block;
  background-image: url(images/octocat-spinner-128.gif);
  background-repeat: no-repeat;
  background-size: cover;
}
.loading-spinner-tiny.inline-block {
  display: inline-block;
}
/*+++++++++++++++
  + Loading Bar +
  +++++++++++++++*/
progress {
  -webkit-appearance: none;
  height: 8px;
  border-radius: 4px;
  background-color: #3b4252;
  box-shadow: inset 0 0 0 1px #d8dee9;
}
progress::-webkit-progress-bar {
  background-color: transparent;
}
progress::-webkit-progress-value {
  border-radius: 4px;
  background-color: #88c0d0;
}
progress:indeterminate {
  background-image: linear-gradient(-45deg, transparent 33%, #88c0d0 33%, #88c0d0 66%, transparent 66%);
  background-size: 24px 8px, 100% 100%, 100% 100%;
  -webkit-animation: progress-buffering 5s linear 6;
}
@-webkit-keyframes progress-buffering {
  100% {
    background-position: -100px 0px;
  }
}
