.tab-bar {
  background: #2e3440;
  width: 100%;
  height: 40px;
  padding: 0;
  display: table;
  table-layout: fixed;
  -webkit-transform: scale(1);
}
.tab-bar .tab {
  color: #d8dee9;
  font-family: "BlinkMacSystemFont", "System Font", "Helvetica Neue", "Roboto", "Cantarell", "Ubuntu", "Lucida Grande", "Segoe UI", "Noto Sans", sans-serif;
  font-size: 14px;
  font-weight: 400;
  text-align: center;
  color: rgba(216, 222, 233, 0.4);
  background: #2e3440;
  display: table-cell;
  width: 100%;
  height: 40px;
  line-height: 40px;
  box-sizing: border-box;
  border: 0;
  padding-left: 20px !important;
  padding-right: 20px !important;
  position: relative;
  transition: color 400ms ease, border 400ms ease, background 400ms ease;
}
.tab-bar .tab .status-added,
.tab-bar .tab .status-ignored,
.tab-bar .tab .status-modified {
  transition: color 400ms ease, background 400ms ease;
}
.tab-bar .tab .status-modified {
  color: #ebcb8b;
}
.tab-bar .tab .status-added {
  color: #a3be8c;
}
.tab-bar .tab:hover {
  color: #eceff4;
  background: #3b4252;
}
.tab-bar .tab:hover .close-icon {
  display: block;
}
.tab-bar .tab.active,
.tab-bar .tab:hover.active {
  color: #eceff4;
  background-color: #3b4252;
  width: 100%;
}
.tab-bar .tab.active:only-child,
.tab-bar .tab:hover.active:only-child {
  background-color: #2e3440;
}
.tab-bar .tab.active.title,
.tab-bar .tab:hover.active.title {
  color: #eceff4;
  padding: 0;
}
.tab-bar .tab.active .status-modified,
.tab-bar .tab:hover.active .status-modified {
  color: #ebcb8b;
}
.tab-bar .tab.active .status-added,
.tab-bar .tab:hover.active .status-added {
  color: #a3be8c;
}
.tab-bar .tab.active .status-ignored,
.tab-bar .tab:hover.active .status-ignored {
  color: rgba(216, 222, 233, 0.4);
}
.tab-bar .tab.active .close-icon:before,
.tab-bar .tab:hover.active .close-icon:before {
  color: rgba(216, 222, 233, 0.4);
}
.tab-bar .tab.active .close-icon:hover:before,
.tab-bar .tab:hover.active .close-icon:hover:before {
  color: #eceff4;
}
.tab-bar .tab.modified:hover .close-icon:before {
  color: #88c0d0;
}
.tab-bar .tab.modified:not(:hover) .close-icon {
  opacity: 1;
  transform: scale(1);
  top: 17px;
  right: 8px;
  display: inline-block;
  border-color: #88c0d0;
  border-width: 2px;
}
.tab-bar .tab .close-icon {
  top: 12px;
  height: 16px;
  width: 16px;
  left: auto;
  right: 4px;
  position: absolute;
  transform: scale(0);
  opacity: 0;
  border-radius: 2px;
  transition: transform 400ms ease, color 400ms ease, background 400ms ease;
}
.tab-bar .tab .close-icon:before {
  color: #eceff4;
  font-size: 12px;
  position: absolute;
  top: 2px;
  left: 2px;
  width: 13px;
  height: 12px;
  transition: transform 400ms ease, color 400ms ease, background 400ms ease;
}
.tab-bar .tab:hover .close-icon {
  transform: scale(1);
  opacity: 1;
}
.platform-darwin .tab .close-icon {
  left: 4px !important;
  right: auto !important;
}
.platform-darwin .tab.modified:not(:hover) .close-icon {
  left: 8px !important;
  right: auto !important;
}
@media (max-width: 1280px), (max-height: 800px) {
  .tab-bar {
    height: 32px;
  }
  .tab-bar .tab {
    height: 32px;
    line-height: 32px;
  }
  .tab-bar .tab.modified:not(:hover) .close-icon {
    top: 13px;
  }
  .tab-bar .tab .close-icon {
    top: 8px;
  }
}
