.tooltip {
  white-space: nowrap;
}
.tooltip .keystroke {
  color: #d8dee9;
  font-family: "BlinkMacSystemFont", "System Font", "Helvetica Neue", "Roboto", "Cantarell", "Ubuntu", "Lucida Grande", "Segoe UI", "Noto Sans", sans-serif;
  font-size: 14px;
  font-weight: 400;
  color: #88c0d0;
  background: #2e3440;
  font-size: 90%;
  border: 1px solid #88c0d0;
  border-radius: 2px;
  padding: 2px 4px;
}
.tooltip.in {
  opacity: 1;
}
.tooltip .tooltip-inner {
  color: #d8dee9;
  font-family: "BlinkMacSystemFont", "System Font", "Helvetica Neue", "Roboto", "Cantarell", "Ubuntu", "Lucida Grande", "Segoe UI", "Noto Sans", sans-serif;
  font-size: 14px;
  font-weight: 400;
  line-height: 26px;
  border-radius: 4px;
  background-color: #d8dee9;
  color: #2e3440 !important;
  white-space: nowrap;
  max-width: none;
  box-shadow: 0 12px 48px 0 rgba(0, 0, 0, 0.4) !important;
}
.tooltip.top .tooltip-arrow {
  border-top-color: #d8dee9;
}
.tooltip.top-left .tooltip-arrow {
  border-top-color: #d8dee9;
}
.tooltip.top-right .tooltip-arrow {
  border-top-color: #d8dee9;
}
.tooltip.right .tooltip-arrow {
  border-right-color: #d8dee9;
}
.tooltip.left .tooltip-arrow {
  border-left-color: #d8dee9;
}
.tooltip.bottom .tooltip-arrow {
  border-bottom-color: #d8dee9;
}
.tooltip.bottom-left .tooltip-arrow {
  border-bottom-color: #d8dee9;
}
.tooltip.bottom-right .tooltip-arrow {
  border-bottom-color: #d8dee9;
}
/*
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
title      Tree View                                       +
project    nord-atom-ui                                    +
repository https://github.com/arcticicestudio/nord-atom-ui +
author     Arctic Ice Studio                               +
email      development@arcticicestudio.com                 +
copyright  Copyright (C) 2016                              +
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
*/
.tree-view {
  font-size: 14px;
  background: #2e3440;
  padding-top: 0;
}
.tree-view .project-root {
  padding-bottom: 5px;
}
.tree-view .project-root:before {
  height: 40px;
  background-clip: padding-box;
}
.tree-view .project-root > .header .name {
  color: #d8dee9;
  font-family: "BlinkMacSystemFont", "System Font", "Helvetica Neue", "Roboto", "Cantarell", "Ubuntu", "Lucida Grande", "Segoe UI", "Noto Sans", sans-serif;
  font-size: 14px;
  font-weight: 400;
  font-weight: 600;
  color: #eceff4;
  height: 41px;
  line-height: 40px;
}
.tree-view .project-root > .header:before {
  color: #eceff4;
  transition: all 400ms ease;
  opacity: 0;
}
.tree-view .project-root > .header:hover:before {
  opacity: 1;
}
.tree-view .project-root.project-root.collapsed > .header:before {
  opacity: 0;
}
.tree-view .project-root.project-root.collapsed > .header:hover:before {
  opacity: 1;
}
.list-group .icon,
.list-tree .icon {
  display: inline-block;
  height: inherit;
}
.list-group .icon::before,
.list-tree .icon::before {
  top: initial;
  line-height: inherit;
  height: inherit;
  vertical-align: top;
}
.tree-view-resizer .tree-view-resize-handle {
  width: 8px;
}
@media (max-width: 1280px), (max-height: 800px) {
  .tree-view .project-root.project-root:before {
    height: 32px;
  }
  .tree-view .project-root.project-root > .header .name {
    height: 33px;
    line-height: 32px;
  }
}
