*/
atom-panel.modal.overlay.from-top.expose-panel {
  top: 0 !important;
  border: 0 !important;
}
.expose-top {
  height: 26px;
}
.expose-top a.icon-gear,
.expose-top a.icon-x {
  color: #3b4252;
  transition: all 400ms ease;
}
.expose-top a.icon-gear:before,
.expose-top a.icon-x:before {
  font-size: 16px !important;
}
.expose-top a.icon-gear:hover,
.expose-top a.icon-x:hover {
  color: #d8dee9;
}
.expose-view .tab-list .expose-tab {
  border-radius: 4px !important;
  box-shadow: 0 12px 48px 0 rgba(0, 0, 0, 0.4) !important;
}
.expose-view .tab-list .expose-tab.active .tab-body {
  border-style: solid;
  border-color: #88c0d0 !important;
}
.expose-view .tab-list .expose-tab .tab-body {
  border-top: 1px solid #88c0d0 !important;
  cursor: default;
}
.expose-view .tab-list .expose-tab .tab-header {
  transition: background 400ms ease;
  color: rgba(216, 222, 233, 0.4);
}
.expose-view .tab-list .expose-tab .tab-header .title {
  transition: color 400ms ease;
}
.expose-view .tab-list .expose-tab .tab-header .close-icon {
  cursor: default;
  height: 16px;
  width: 16px;
  color: rgba(216, 222, 233, 0.4);
  transition-duration: 400ms;
  transition: color 400ms ease;
}
.expose-view .tab-list .expose-tab:hover .tab-header {
  background: #3b4252;
}
.expose-view .tab-list .expose-tab:hover .tab-header .title {
  color: #eceff4;
}
.expose-view .tab-list .expose-tab:hover .tab-header .close-icon.icon-x {
  color: #eceff4;
}
.expose-view .tab-list .expose-tab:hover .tab-body {
  box-shadow: none;
}
