button {
  display: inline-block;
}
.btn,
button {
  padding: 0;
  height: 2.25rem;
  line-height: 2.25rem;
  padding: 0 1rem;
  background: #1c1d21;
  transition: box-shadow 250ms cubic-bezier(0.4, 0, 0.2, 1), background 250ms cubic-bezier(0.4, 0, 0.2, 1);
  text-transform: uppercase;
  color: rgba(203, 205, 210, 0.8);
  outline: none;
  border: none;
  margin: 0.25rem 0;
  vertical-align: middle;
  box-shadow: 0 0.0625rem 0.25rem rgba(0, 0, 0, 0.12), 0 0.0625rem 0.25rem rgba(0, 0, 0, 0.24);
}
.btn:hover,
button:hover {
  color: rgba(203, 205, 210, 0.8);
  background: #1c1d21;
  box-shadow: 0 0.2rem 0.375rem rgba(0, 0, 0, 0.16), 0 0.2rem 0.375rem rgba(0, 0, 0, 0.23);
}
.btn:active,
button:active,
.btn:focus,
button:focus {
  color: rgba(203, 205, 210, 0.8);
  background: #202125;
  outline: none;
  border: none;
  box-shadow: 0 0.2rem 0.375rem rgba(0, 0, 0, 0.16), 0 0.2rem 0.375rem rgba(0, 0, 0, 0.23);
}
.btn.icon::before,
button.icon::before {
  margin-right: 1rem;
}
.btn[disabled],
button[disabled],
.btn.disabled,
button.disabled {
  opacity: 0.5;
  background: #17171a;
  pointer-events: none;
  box-shadow: none;
}
.btn-group .btn,
.btn-group button {
  margin: 0;
  box-shadow: none;
  border: none;
}
.btn-group .btn:only-child,
.btn-group button:only-child {
  box-shadow: 0 0.0625rem 0.25rem rgba(0, 0, 0, 0.12), 0 0.0625rem 0.25rem rgba(0, 0, 0, 0.24);
}
.btn-group .btn:only-child:hover,
.btn-group button:only-child:hover {
  box-shadow: 0 0.2rem 0.375rem rgba(0, 0, 0, 0.16), 0 0.2rem 0.375rem rgba(0, 0, 0, 0.23);
}
.btn-group .btn:only-child[disabled],
.btn-group button:only-child[disabled],
.btn-group .btn:only-child.disabled,
.btn-group button:only-child.disabled {
