atom-text-editor[mini],
atom-text-editor[mini]::shadow {
  position: relative;
  color: rgba(203, 205, 210, 0.8);
  background-color: transparent;
  border: none;
  border-radius: 0;
  font-size: 0.875rem;
  line-height: 1.25rem;
  padding: 0.75rem 0;
  max-height: none;
  box-shadow: inset 0 -1px 0 0 rgba(203, 205, 210, 0.1);
  transition: all 250ms ease;
}
atom-text-editor[mini]::before,
atom-text-editor[mini]::shadow::before {
  content: '';
  position: absolute;
  height: 2px;
  background: #009688;
  bottom: 0;
  top: auto;
  left: 0;
  right: 0;
  transform: scaleX(0);
}
.use-animations atom-text-editor[mini]::before,
.use-animations atom-text-editor[mini]::shadow::before {
  transition: transform 300ms;
}
