css:
/* Fade-out "On Changes" setting when colours are disabled */
.file-icons-colourless section.settings-panel label[for="file-icons.onChanges"] input[type="checkbox"]:checked {
  background-color: #aaa;
}
.file-icons-colourless section.settings-panel label[for="file-icons.onChanges"],
.file-icons-colourless section.settings-panel label[for="file-icons.onChanges"] ~ .setting-description {
  pointer-events: none;
  opacity: .4;
}
