atom-notifications:empty {
  pointer-events: none;
}
atom-text-editor[with-minimap]::shadow .editor--private,
atom-text-editor[with-minimap] .editor--private {
  order: 2;
  flex: 1 0 90%;
  width: 90%;
}
atom-text-editor::shadow atom-text-editor-minimap,
atom-text-editor atom-text-editor-minimap,
html atom-text-editor-minimap {
  display: block;
  overflow: hidden;
  position: relative;
  -webkit-user-select: none;
}
atom-text-editor::shadow atom-text-editor-minimap:not([stand-alone]),
atom-text-editor atom-text-editor-minimap:not([stand-alone]),
html atom-text-editor-minimap:not([stand-alone]) {
  height: 100%;
  order: 3;
  width: 10%;
  flex: 0 0 10%;
}
atom-text-editor::shadow atom-text-editor-minimap:not([stand-alone]).left,
atom-text-editor atom-text-editor-minimap:not([stand-alone]).left,
html atom-text-editor-minimap:not([stand-alone]).left {
  order: 1;
}
atom-text-editor::shadow atom-text-editor-minimap.absolute,
atom-text-editor atom-text-editor-minimap.absolute,
html atom-text-editor-minimap.absolute {
  position: absolute;
  right: 0;
}
atom-text-editor::shadow atom-text-editor-minimap.absolute.adjust-absolute-height,
atom-text-editor atom-text-editor-minimap.absolute.adjust-absolute-height,
html atom-text-editor-minimap.absolute.adjust-absolute-height {
  pointer-events: none;
}
atom-text-editor::shadow atom-text-editor-minimap.absolute.adjust-absolute-height::shadow canvas,
atom-text-editor atom-text-editor-minimap.absolute.adjust-absolute-height::shadow canvas,
html atom-text-editor-minimap.absolute.adjust-absolute-height::shadow canvas {
  pointer-events: auto;
}
atom-text-editor::shadow atom-text-editor-minimap.absolute.left,
atom-text-editor atom-text-editor-minimap.absolute.left,
html atom-text-editor-minimap.absolute.left {
  position: relative;
  right: initial;
}
atom-text-editor::shadow atom-text-editor-minimap.absolute .open-minimap-quick-settings,
atom-text-editor atom-text-editor-minimap.absolute .open-minimap-quick-settings,
html atom-text-editor-minimap.absolute .open-minimap-quick-settings {
  right: 16px;
}
atom-text-editor::shadow atom-text-editor-minimap::shadow canvas,
atom-text-editor atom-text-editor-minimap::shadow canvas,
html atom-text-editor-minimap::shadow canvas {
  position: absolute;
  top: 0;
  left: 0;
  transform-origin: 0 0;
}
atom-text-editor::shadow atom-text-editor-minimap::shadow .minimap-visible-area,
atom-text-editor atom-text-editor-minimap::shadow .minimap-visible-area,
html atom-text-editor-minimap::shadow .minimap-visible-area {
  position: absolute;
  display: block;
  cursor: -webkit-grab;
  border-left: 0px solid rgba(127, 127, 127, 0.1);
}
atom-text-editor::shadow atom-text-editor-minimap::shadow .minimap-visible-area:active,
atom-text-editor atom-text-editor-minimap::shadow .minimap-visible-area:active,
html atom-text-editor-minimap::shadow .minimap-visible-area:active {
  cursor: -webkit-grabbing;
}
atom-text-editor::shadow atom-text-editor-minimap::shadow .minimap-visible-area::after,
atom-text-editor atom-text-editor-minimap::shadow .minimap-visible-area::after,
html atom-text-editor-minimap::shadow .minimap-visible-area::after {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-color: rgba(127, 127, 127, 0.2);
}
atom-text-editor::shadow atom-text-editor-minimap::shadow .minimap-controls,
atom-text-editor atom-text-editor-minimap::shadow .minimap-controls,
html atom-text-editor-minimap::shadow .minimap-controls {
  position: relative;
  height: 100%;
  pointer-events: none;
}
atom-text-editor::shadow atom-text-editor-minimap::shadow .minimap-scroll-indicator,
atom-text-editor atom-text-editor-minimap::shadow .minimap-scroll-indicator,
html atom-text-editor-minimap::shadow .minimap-scroll-indicator {
  position: absolute;
  display: block;
  right: 0;
  width: 2px;
  min-height: 2px;
  z-index: 10;
  background: #3b4252;
}
atom-text-editor::shadow atom-text-editor-minimap::shadow .open-minimap-quick-settings,
atom-text-editor atom-text-editor-minimap::shadow .open-minimap-quick-settings,
html atom-text-editor-minimap::shadow .open-minimap-quick-settings {
  opacity: 0;
  position: absolute;
  z-index: 1;
  cursor: pointer;
  pointer-events: auto;
  color: #d8dee9;
  font-size: 20px;
  text-align: center;
  line-height: 24px;
  width: 24px;
  height: 24px;
  display: block;
  right: 8px;
  transition: opacity 0.4s;
}
atom-text-editor::shadow atom-text-editor-minimap::shadow .open-minimap-quick-settings:before,
atom-text-editor atom-text-editor-minimap::shadow .open-minimap-quick-settings:before,
html atom-text-editor-minimap::shadow .open-minimap-quick-settings:before {
  content: '\f02f';
  font-family: 'Octicons Regular';
}
atom-text-editor::shadow atom-text-editor-minimap:hover::shadow .open-minimap-quick-settings,
atom-text-editor atom-text-editor-minimap:hover::shadow .open-minimap-quick-settings,
html atom-text-editor-minimap:hover::shadow .open-minimap-quick-settings {
  opacity: 1;
  transition: opacity 0.1s;
}
minimap-quick-settings {
  position: absolute !important;
  z-index: 5;
}
minimap-quick-settings .hidden-input {
  position: absolute;
  width: 0;
  height: 0;
  background: transparent;
  border: none;
}
minimap-quick-settings ol {
  margin-top: 0 !important;
}
minimap-quick-settings ol .separator {
  background: #434c5e;
  height: 1px;
}
minimap-quick-settings ol .separator:first-child {
  display: none;
}
minimap-quick-settings ol li:hover {
  background: #434c5e;
}
minimap-quick-settings .select-list.popover-list ol.list-group {
  overflow: visible;
  max-height: none;
}
minimap-quick-settings .btn-group {
  width: 100%;
}
minimap-quick-settings .btn-group .btn {
  width: 50%;
}
