var expect = require('chai').expect;

module.exports = function (helpers) {
    var SVG_FLAGS = 1; // SVG

    var svg = helpers.vdom.createElement('svg', null /* attrs */, null /* child count */, SVG_FLAGS);

    var docFragment = helpers.vdom.createDocumentFragment();
    svg.aD_(docFragment);

    expect(svg.aS_).to.equal('http://www.w3.org/2000/svg');

    var docFragmentClone = docFragment.aG_();
    expect(docFragmentClone._c_).to.equal(null);
    expect(docFragmentClone.aM_ == null).to.equal(true);

    return svg;
};