var expect = require('chai').expect;

module.exports = function (helpers) {
    var link = helpers.vdom.createElement('a', { 'href': 'http://ebay.com' }, 1 /* childCount */).t('eBay');

    var el = helpers.vdom.createElement('div', { 'class': 'foo', 'onclick': 'doSomething()' }, 2 /* childCount */).n(link).e('span', null, 0);

    var linkClone = el._a_;

    expect(linkClone).to.not.equal(link);
    expect(link.aM_).to.equal(null);
    expect(link._c_).to.equal(null);

    expect(linkClone._c_._b_).to.equal('span');
    expect(linkClone.aM_._b_).to.equal('div');

    return el;
};